import { useState, useEffect } from 'react'
import '../navbar/navbar.css'
import { HashLink } from 'react-router-hash-link'
import { NavLink, useLocation } from 'react-router-dom'
interface NavbarProps {
    links: {
        text: string
        url: string
    }[]
}

export const Navbar: React.FC<NavbarProps> = ({ links }) => {
    
    const [scroll, setScroll] = useState(true);
    const [hash, setHash] = useState(useLocation());
    const isActive = (iHash: any) => hash === iHash;

    useEffect(() => {
        window.addEventListener("scroll", () => {
            setScroll(window.scrollY < 50);
        });
    }, []);

    useEffect(() => {
        window.addEventListener("scroll", () => {
          
        });
    }, []);
    
    return(
        <nav className={scroll ? 'navbar-nav' : 'navbar-nav scroll-navbar'}>
            {links.map((link: any) =>
                <HashLink className='prevent-select active-class' style={
                    isActive(link.url)
                      ? {
                          color: "red"
                        }
                      : {}
                  } key={link.url} smooth to={link.url}>{link.text}</HashLink>
            )}
        </nav>
    )
}
